import React from 'react';

const Hero = () => {
  return (
    <div className="relative h-screen bg-cover bg-center" style={{ backgroundImage: "url('https://source.unsplash.com/random')" }}>
      <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col justify-center items-center text-center p-4">
        <h1 className="text-white text-4xl md:text-6xl font-bold mb-4">
          Bienvenidos a Mi Página
        </h1>
        <p className="text-white text-lg md:text-2xl mb-8">
          Esto es un ejemplo de un hero con Tailwind CSS
        </p>
        <button className="bg-primary text-white px-6 py-3 rounded-lg font-medium text-lg hover:bg-opacity-80 transition">
          Aprende Más
        </button>
      </div>
    </div>
  );
};

export default Hero;
